unit.select:
				select
          tc_janal_definiciones_avances.id_definicion_avance id_key_definicion_avance, tc_janal_definiciones_avances.*
        from
          tc_janal_definiciones_avances
        where
          id_grupo= {idGrupo}
					and orden= {orden}
					and id_definicion_avance <> {idDefinicionAvance}
				order by
					orden
