unit.select:
        select      
          *
        from      
          tc_mantic_masivas_archivos
        where      
				  id_empresa= {idEmpresa} 
				  and id_tipo_masivo= {idTipoMasivo}
          and id_masiva_archivo!= {idMasivaArchivo}

select
	tc_mantic_almacenes_ubicaciones.id_almacen_ubicacion as id_key,
	tc_mantic_almacenes_ubicaciones.*
from 
	tc_mantic_almacenes_ubicaciones
where
	id_almacen= {idAlmacen}
  {condicion}
	and nivel >= {nivel}

        select      
          *
        from      
          tc_mantic_transferencias_bitacora
        where      
          id_usuario= {idUsuario} 
          and id_transferencia_estatus= {idTransferenciaEstatus} 
          and id_transferencia= {idTransferencia}
				  and date_format(registro, '%Y%m%d%H%i%S')= '{registro}'
